mapper.select:
select rno,free_seq,fk_userid,name,title,viewcount,writedate,filename from ( select rownum as rno,free_seq,fk_userid,name,title,viewcount,writedate,filename from ( select free_seq,fk_userid,name,title,viewcount,to_char(writedate,'yyyy-mm-dd hh24:mi:ss') as writedate,filename from free_board where status = 1
and ${searchType} like '%'|| #{searchWord} ||'%'
order by free_seq desc ) V ) T where rno between #{startRno} and #{endRno}

select comment_seq, name, content, writedate
from
(
	select row_number() over(order by comment_seq desc) as rno, comment_seq,name,content,to_char(writedate, 'yyyy-mm-dd hh24:mi:ss') as writedate
	from free_board_comment
	where status = 1 and parentSeq = #{parentSeq}
	order by comment_seq desc
) V
where rno between #{startRno} and #{endRno}

 	select count(*)
	from free_board_comment
	where status = 1 and parentSeq = #{parentSeq}

mapper.update:
update free_board set title = #{title}
					 ,content = #{content}
					 ,writedate = sysdate
where free_seq = #{free_seq} and password = #{password}
