mapper.select:
select lecNum, lecTitle, to_char(lecStartday, 'yyyy-mm-dd') AS lecStartday, to_char(lecEndday, 'yyyy-mm-dd') AS lecEndday, subName, fk_subSeq from lecture_tbl A join subject_tbl B on A.fk_subSeq = B.subSeq where fk_subSeq = #{fk_subSeq}
and lecTitle like '%'|| #{searchWord} ||'%'
order by lecNum desc

select lecTitle, lecLink, lecStartday, lecEndday
from lecture_tbl
where lecNum = #{lecSeq}
